# Create a mapping dictionary
class_mapping = dict(zip(old_classes, new_classes))

# Read the JSON file
with open('imagenet_questions.jsonl', 'r') as f:
    data = [json.loads(line) for line in f]

# Replace the old classes with the new ones
for item in data:
    old_class = item['object_name']
    if old_class in class_mapping:
        item['object_name'] = class_mapping[old_class]

# Write the modified data back to a new JSON file
with open('imagenet_questions_new_classes.jsonl', 'w') as f:
    for item in data:
        f.write(json.dumps(item) + '\n')

